import { useState } from "react";
function SingleCat({ name, latinName, img }) {
  return (
    <>
      <li>
        {" "}
        <h3>{name}</h3>
        <span>({latinName})</span>
        <img
          src={img}
          alt={"Cat Image"}
          style={{ display: "block", margin: "0 auto" }}
        />
      </li>
    </>
  );
}

const cats = [
  {
    name: "Cheetah",
    latinName: "Acinonyx jubatus",
    img: "data:image/jpeg;base64,",
    family: "Felidae",
  },
  {
    name: "Cougar",
    latinName: "Puma concolor",
    img: "data:image/jpeg;base64,",
    family: "Felidae",
  },
  {
    name: "Jaguar",
    latinName: "Panthera onca",
    img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTQqjWL-RloLBbiO6qPjZQj3D5B9nDM9FfKPg&s",
    family: "Panthera",
  },
  {
    name: "Leopard",
    latinName: "Panthera pardus",
    img: "data:image/jpeg;base64,",
    family: "Panthera",
  },
  {
    name: "Lion",
    latinName: "Panthera leo",
    img: "data:image/png;base64,",
    family: "Panthera",
  },
  {
    name: "Snow leopard",
    latinName: "Panthera uncia",
    img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRCbRxG3GAF0s92rxWdmalHZ2PTUmlyV1XhvA&s",
    family: "Panthera",
  },
  {
    name: "Tiger",
    latinName: "Panthera tigris",
    img: "data:image/jpeg;base64,",
    family: "Panthera",
  },
];

// Bigcats Component
function Bigcats() {
  const [catsList, setCatsList] = useState(cats);

  // Sorting function
  const sort = () => {
    const sortedCats = [...catsList].sort((a, b) =>
      a.name.localeCompare(b.name)
    );
    setCatsList(sortedCats);
  };

  // Reverse list function
  const reverseList = () => {
    const reversedCats = [...catsList].reverse();
    setCatsList(reversedCats);
  };

  // Filter Panthera family function
  const filter = () => {
    const Panthera = cats.filter((cat) => cat.family === "Panthera");
    setCatsList(Panthera);
  };

  // Reset function to the original list
  const resetList = () => {
    setCatsList(cats);
  };

  // Function to add a new cat
  const addCat = (newCat) => {
    setCatsList((prevCatsList) => [...prevCatsList, newCat]);
  };

  return (
    <div>
      <AddCatForm onAddCat={addCat} />{" "}
      {/* Pass the addCat function to the AddCatForm */}
      <div>
        <button onClick={sort}>Sort Alphabetical</button>
        <button onClick={reverseList}>Reverse List</button>
        <button onClick={filter}>Show Panthera Cats</button>
        <button onClick={resetList}>Reset List</button>
      </div>
      <ul>
        {catsList.map((cat, index) => (
          <SingleCat
            key={index}
            name={cat.name}
            latinName={cat.latinName}
            img={cat.img}
          />
        ))}
      </ul>
    </div>
  );
}

// AddCatForm Component
function AddCatForm({ onAddCat }) {
  const [catName, setCatName] = useState("");
  const [catLatinName, setCatLatinName] = useState("");
  const [img, setImg] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    const newCat = {
      name: catName,
      latinName: catLatinName,
      img: img,
    };
    onAddCat(newCat); // Pass the new cat to parent Bigcats
    setCatName(""); // Clear form fields
    setCatLatinName("");
    setImg("");
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Cat Name:
        <input
          type="text"
          value={catName}
          onChange={(e) => setCatName(e.target.value)}
        />
      </label>
      <label>
        Latin Name:
        <input
          type="text"
          value={catLatinName}
          onChange={(e) => setCatLatinName(e.target.value)}
        />
      </label>
      <label>
        Image URL:
        <input
          type="url"
          value={img}
          onChange={(e) => setImg(e.target.value)}
        />
      </label>
      <button type="submit">Add Cat</button>
    </form>
  );
}

export default Bigcats;
